$('#query-field,#query-suffix').keydown(function(e){
	if(e.keyCode==13){
	   search_domain();
	}
});

$('#define-btn').click(function(){
	search_domain();
});

function search_domain(){
	$('#status-msg').html("查询中").show();
	var q = $('#query-field').val();
	var suffix = $('#query-suffix').val();
	if(!q || !suffix){
		$('#status-msg').html("域名或后缀不能为空").show();
		return false;
	}
	var url = 'http://pandavip.www.net.cn/check/check_ac1.cgi?domain='+q+'.'+suffix;
	// var url = 'http://whois.hichina.com/whois/api_whois?host='+q+'.'+suffix;
	var xhr = new XMLHttpRequest();
	xhr.open("GET", url, true);
	xhr.onreadystatechange = function() {
	　　if (xhr.readyState == 4) {
			var msg = xhr.responseText;
			var result = '';
			var status = msg.split("|");
			if(status[2] == 211){
				result += '已注册 <a href="http://whois.hichina.com/whois/domain/'+q+'.'+suffix+'" target="_blank">whois</a>';
			}else if (status[2] == 210){
				result += '可以注册';
			}else{
				result += '暂未支持';
			}
			$('#status-msg').html(result);
			$('#domain-name').text(q);
			$('#domain-result').html(msg);
			$('#domain-wanwang').attr('href','http://www.net.cn/domain/searchresult/?keyword='+q+'&suffix=.com&domaintype=en#searchAnchor')
			$('#meaning').show();
	　　}
	}
	xhr.send();
} 